graphql.query:
{
  posts {
    edges {
      node {
        date
        link
        title
      }
    }
  }
}

{
  posts(first: 100) {
    edges {
      node {
        author {
          node {
            firstName
            name
          }
        }
        link
        title
        featuredImage {
          node {
            altText
            sourceUrl
          }
        }
        categories {
          edges {
            node {
              slug
              name
              link
            }
          }
        }
      }
    }
  }
}

{
  post(id: 1787, idType: DATABASE_ID) {
    databaseId
    title
    link
    featuredImage {
      node {
        sourceUrl
      }
    }
    content
  }
}

{
  recipes {
    edges {
      node {
        title
        content
        rating {
          rating
          fieldGroupName
        }
      }
    }
  }
}

{
  books {
    edges {
      node {
        title
        rating {
          fieldGroupName
          rating
        }
        content
      }
    }
  }
}

{
  users {
    edges {
      node {
        firstName
        name
        slug
        username
      }
    }
  }
}

mutation CREATE_POST {
  createPost(input: {
    clientMutationId: "CreatePost"
    title: "GraphQL Post Title"
    content: "WPGraphQL is a free, open-source WordPress plugin that provides an extendable GraphQL schema and API for any WordPress site."
    status: PUBLISH
  }) {
    post {
      id
      title
      content
      status
    }
  }
}

mutation UPDATE_POST {
  updatePost(input: {
    clientMutationId: "UpdatePost",
    id: "cG9zdDoxOTcw",
    title: "Better title!"
  }) {
    post {
      id
      title
      date
      link
      content
    }
  }
}

mutation DELETE_POST {
  deletePost(input: {
    clientMutationId:"DeletePost"
    id:"cG9zdDoxOTcw"
  }) {
    deletedId
    post {
      id
    }
  }
}

